    // Initialize matrices with random values
   ...

    // Measure execution time of naive implementation
    clock_t start = clock();
    naiveMatrixMult(A, B, C);
    clock_t end = clock();
    double naiveTime = (double)(end - start) / CLOCKS_PER_SEC;
    printf("Naive implementation time: %f seconds\n", naiveTime);

    // Measure execution time of cache-efficient implementation
    start = clock();
    blockedMatrixMult(A, B, C, blockSize);
