class Board:
    def __init__(self, size=3):
        self.size = size
        self.squares = [[' ']*size for _ in range(size)]
        self.is_winner = False

    def __str__(self):
        return '\n'.join([' '.join(row) for row in self.squares])

    def mark_square(self, x, y, player):
        if self.squares[x][y] =='':
            self.squares[x][y] = player.symbol
            self.check_winner()
        else:
            print("Square is already occupied!")

    def available_squares(self):
        return [(x, y) for x in range(self.size) for y in range(self.size) if self.squares[x][y] =='']

    def check_winner(self):
        # Check rows and columns
        for i in range(self.size):
            if self.squares[i][0] == self.squares[i][1] == self.squares[i][2]!='':
                self.is_winner = True
                return
            if self.squares[0][i] == self.squares[1][i] == self.squares[2][i]!='':
                self.is_winner = True
                return
        # Check diagonals
        if self.squares[0][0] == self.squares[1][1] == self.squares[2][2]!='':
            self.is_winner = True
            return
        if self.squares[0][2] == self.squares[1][1] == self.squares[2][0]!='':
            self.is_winner = True
            return

class Game:
    def __init__(self, player1, player2):
        self.board = Board()
        self.player1 = player1
        self.player2 = player2
        self.current_player = player1

    def play(self):
        while True:
            print(self.board)
            self.current_player.move(self.board)
            if self.board.is_winner:
                print(f"{self.current_player.name} wins!")
                break
            self.current_player = self.player2 if self.current_player == self.player1 else self.player1

