            j = i + 1
            while j < len(s) and s[j]!= ')':
                j += 1
            if j < len(s) and s[j] == ')':
                if j == i + 1:
                    changed = True
                else:
                    new_s += s[i+1:j]
            else:
                new_s += s[i]
            i = j + 1
        else:
            new_s += s[i]
